// Chunk the document text into smaller pieces for better embedding and retrieval
const textContent = $json.textContent;
const metadata = $json.metadata;
const fileName = $json.fileName;
const processingId = $json.processingId;

// Chunking configuration
const CHUNK_SIZE = 1000; // characters per chunk
const CHUNK_OVERLAP = 200; // overlap between chunks

// Function to split text into sentences
function splitIntoSentences(text) {
  return text.match(/[^.!?]+[.!?]+/g) || [text];
}

// Function to create chunks with overlap
function createChunks(text, chunkSize, overlap) {
  const chunks = [];
  let start = 0;

  while (start < text.length) {
    let end = Math.min(start + chunkSize, text.length);

    // Try to end at a sentence boundary
    if (end < text.length) {
      const lastSentenceEnd = text.lastIndexOf('.', end);
      const lastQuestionEnd = text.lastIndexOf('?', end);
      const lastExclamationEnd = text.lastIndexOf('!', end);

      const sentenceEnd = Math.max(lastSentenceEnd, lastQuestionEnd, lastExclamationEnd);
      if (sentenceEnd > start + (chunkSize * 0.5)) {
        end = sentenceEnd + 1;
      }
    }

    const chunk = text.substring(start, end).trim();
    if (chunk.length > 50) { // Only include meaningful chunks
      chunks.push({
        text: chunk,
        startIndex: start,
        endIndex: end,
        chunkIndex: chunks.length
      });
    }

    start = end - overlap;
    if (start >= text.length) break;
  }

  return chunks;
}

// Create chunks
const chunks = createChunks(textContent, CHUNK_SIZE, CHUNK_OVERLAP);

// Prepare chunk data with metadata
const processedChunks = chunks.map((chunk, index) => {
  const chunkId = `${processingId}_chunk_${index}`;

