const llmResponse = $input.all()[0].json;
const processedResults = $('Process Search Results').all()[0].json;

// Extract the generated response
let rawResponse = llmResponse.response || "Unable to generate response";

// Function to clean and extract core response from thinking models
function extractCoreResponse(response) {
  // Remove <think>...</think> blocks (case insensitive)
  let cleaned = response.replace(/<think>[\s\S]*?<\/think>/gi, '');

  // Remove any remaining thinking patterns
  cleaned = cleaned.replace(/^(Let me think|I need to|Let me analyze|Hmm,|So,|Well,|Okay,).*$/gim, '');

  // Remove meta-commentary about the response itself
  cleaned = cleaned.replace(/^(Here is|Here's|I'll provide|Let me provide|Based on).*?:/gim, '');
  cleaned = cleaned.replace(/^(Certainly!|Sure!|Of course!) (Here is|Here's).*?:/gim, '');

  // Remove excessive line breaks and clean up formatting
  cleaned = cleaned.replace(/\n{3,}/g, '\n\n');
  cleaned = cleaned.trim();

  // If response starts with a separator line, clean it up
  cleaned = cleaned.replace(/^---+\s*/g, '');

  // Remove any remaining introductory phrases at the start
  cleaned = cleaned.replace(/^(Based on the provided documents?,?\s*)/i, '');
  cleaned = cleaned.replace(/^(Here is a structured.*?response.*?:?\s*)/i, '');

  return cleaned.trim();
}

// Function to improve response structure and readability
function improveResponseStructure(response) {
  // Ensure proper spacing around headers
  response = response.replace(/^(#{1,6})\s*/gm, '$1 ');

  // Ensure proper spacing around bullet points
  response = response.replace(/^(\d+\.|\*|-)\s*/gm, '$1 ');

  // Clean up any double spaces
  response = response.replace(/  +/g, ' ');

  // Ensure proper paragraph spacing
  response = response.replace(/\n\n\n+/g, '\n\n');

  return response;
}

// Extract and clean the core response
