    if (end < cleanText.length) {
      const sentenceBoundaries = ['.', '!', '?', '\n'];
      let bestBoundary = -1;

      for (const boundary of sentenceBoundaries) {
        const boundaryPos = cleanText.lastIndexOf(boundary, end);
        if (boundaryPos > start + (chunkSize * 0.5)) {
          bestBoundary = Math.max(bestBoundary, boundaryPos);
        }
      }

      if (bestBoundary > -1) {
        end = bestBoundary + 1;
      }
    }

    const chunk = cleanText.substring(start, end).trim();

    if (chunk.length >= MIN_CHUNK_SIZE) {
      chunks.push({
        text: chunk,
        startIndex: start,
        endIndex: end,
        chunkIndex: chunks.length
      });
    }

    // FIXED: Proper start position calculation to prevent infinite loops
    const nextStart = end - overlap;
    if (nextStart <= start) {
      // If overlap would cause us to not move forward, move forward by at least 1 character
      start = start + Math.max(1, chunkSize - overlap);
    } else {
      start = nextStart;
    }

    // Break if we've reached the end
    if (start >= cleanText.length) break;
  }

  return chunks;
}

